import {Component, EventEmitter, Input, OnChanges, OnDestroy, OnInit, Output, SimpleChange, SimpleChanges} from '@angular/core';
import {trigger, transition, state, animate, style, AnimationEvent, query, stagger} from '@angular/animations';
import {interval, Subscription} from 'rxjs';
import {Question} from '../../../models/question';

@Component({
    selector: 'app-check-in',
    templateUrl: './check-in.component.html',
    animations: [
        trigger('check-in', [
            transition('* => *', [ // each time the binding value changes
                query(':enter', [
                    style({
                        left: '-50px',
                        display: 'block'
                    }),
                    stagger(500, [
                        animate('3s', style({
                            left: 'calc(100%)',
                            display: 'block'
                        }))
                    ])
                ], { optional: true })
            ])
        ]),
        trigger('check-out', [
            transition('* => *', [ // each time the binding value changes
                query(':enter', [
                    style({
                        left: 'calc(100%)',
                    }),
                    stagger(500, [
                        animate('3s', style({
                            left: '-50px',
                        }))
                    ])
                ], { optional: true })
            ])
        ])
    ],
    styleUrls: ['./check-in.component.sass']
})
export class CheckInComponent implements OnChanges, OnDestroy {

    readonly WAIT_TIME_IN_SECONDS = 1;

    @Input()
    public question: Question;

    @Input()
    public lobby: string;

    @Input()
    public userToken: string;

    @Input()
    public errorPenalty: boolean;

    @Output()
    public questionAnsweredCorrectly = new EventEmitter();

    @Output()
    public questionAnsweredFalse = new EventEmitter<string>();

    public run = true;
    public currentStep: number;
    public checkInQueue: number[] = [];
    public checkOutQueue: number[] = [];
    public offsetSubscription: Subscription;
    public showIntro = false;
    public showQuestion = false;
    public timeInSecondsTillStart: number;

    public city = 'data:image/jpeg;base64,';
    public hotel = 'data:image/png;base64,';
    private checkinRunArray = [
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,'
    ];
    private checkOutRunArray = [
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,'
    ];
    private offset: number;
    private checkInDone: boolean;
    private checkOutDone: boolean;

    constructor() {
        this.offset = 0;
        this.offsetSubscription = interval(300).subscribe(() => {
            this.offset = (this.offset + 1) % 3;
        });
    }

    ngOnChanges(changes: SimpleChanges): void {
        if (!changes.question) {
            return;
        }
        this.showIntro = true;
        this.showQuestion = false;
        this.timeInSecondsTillStart = undefined;
        let timeForIntro = this.question.introTimer;
        if (!timeForIntro) {
            timeForIntro = this.WAIT_TIME_IN_SECONDS;
        }
        setTimeout(() => {
            this.showIntro = false;
            setTimeout(() => {
                this.currentStep = -1;
                this.checkInDone = true;
                this.checkOutDone = true;
                this.manageQueues();
            }, 1000);
        }, timeForIntro * 1000);
        this.countTimeInSeconds(timeForIntro);
    }

    ngOnDestroy(): void {
        this.offsetSubscription.unsubscribe();
    }

    public getRunningImage(index: number, checkin: boolean) {
        let element;
        if (checkin) {
            element = this.checkInQueue[index];
        } else {
            element = this.checkOutQueue[index];
        }
        const value = ( element + this.offset) % 3;
        if (checkin) {
            return this.checkinRunArray[value];
        } else {
            return this.checkOutRunArray[value];
        }
    }

    public checkedIn(event) {
        if (event.totalTime > 0) {
            this.checkInDone = true;
            this.checkInQueue = [];
            this.manageQueues();
        }
    }

    public checkedOut(event) {
        if (event.totalTime > 0) {
            this.checkOutDone = true;
            this.checkOutQueue = [];
            this.manageQueues();
        }
    }

    private manageQueues() {
        if (!this.checkInDone || !this.checkOutDone) {
            return;
        }
        if (this.currentStep >= this.question.checkInFlows.length - 1) {
            this.showQuestion = true;
            return;
        }
        this.currentStep += 1;
        this.checkInDone = false;
        this.checkOutDone = false;
        setTimeout(() => {
            const checkInFlow = this.question.checkInFlows[this.currentStep];
            for (let i = 0; i < checkInFlow.in; i += 1) {
                this.checkInQueue.push(i);
            }
            for (let i = 0; i < checkInFlow.out; i += 1) {
                this.checkOutQueue.push(i);
            }
            if (checkInFlow.in === 0) {
                this.checkInDone = true;
            }
            if (checkInFlow.out === 0) {
                this.checkOutDone = true;
            }
            if (checkInFlow.in && checkInFlow.out) {
                this.manageQueues();
            }
        }, 100);
    }

    private countTimeInSeconds(time) {
        if (time <= 0) {
            return;
        }
        this.timeInSecondsTillStart = time;
        setTimeout(() => {
            this.countTimeInSeconds(time - 1);
        }, 999);
    }
}
